mapper.update:
UPDATE ${dbName}.day_behave_recordlist
SET today_encourageAdv_count = today_encourageAdv_count + #{encourageCount}
WHERE day_id = #{dayid}

UPDATE ${dbName}.day_behave_recordlist
SET service_CallBack_Reward_Count = service_CallBack_Reward_Count + #{rewardCount},
    totalRed                      = totalRed + #{totalRed},
    todayRed                      = todayRed + #{todayRed}
WHERE day_id = #{dayid}

update ${dbName}.day_behave_recordlist
set server_CallBack_no = server_CallBack_no + #{noCallbackCount}
WHERE day_id = #{dayid}

update ${dbName}.day_behave_recordlist
set service_CallBack_AdvCount = service_CallBack_AdvCount + #{noCallbackCount}
WHERE day_id = #{dayid}
