mapper.update:
update behind_manage.mobile_detail
set device_name = #{deviceName}
where mobile_id = #{mobileId}

update behind_manage.mobile_detail
set mobile_system = #{mobileSystem}
where mobile_id = #{mobileId}

mapper.select:
select mobile_id, mobile_system
from behind_manage.mobile_detail
where mobile_id = mobile_id
  and mobile_cpu = #{cpu}
  and mobile_cpu_fluency = #{cpuFluency}
  and device_detail = #{deviceModel}
  and device_type = #{deviceType}

select mobile_id, device_name, device_type, device_detail, mobile_cpu, mobile_cpu_fluency, mobile_system
from behind_manage.mobile_detail
where mobile_id = #{mobileId,jdbcType=INTEGER}
