mapper.update:
update ad_stream
set clicks= #{changeData.clicks}
where ad_stream_id = #{targetAdv}

update ad_stream
set first_click_time = #{firstClickTime}
where ad_stream_id = #{advId}

mapper.select:
select stream.ad_stream_id, stream.adv_type_id, stream.player_id, stream.app_id, stream.code_bit_id, stream.request_id, stream.adv_ecpm, inet_ntoa(stream.ip) as ip, stream.clicks, stream.first_click_time, stream.request_time, stream.wx_nick_name, stream.errinfo,adn.adn_name as advLegengds from yunting_app_test.ad_stream stream left join behind_manage.admin_codebit code on stream.code_bit_id = code.code_bit_id left join behind_manage.adn on code.adn_id = adn.id where adv_type_id = #{searchDto.advType}
and adn.adn_name=#{searchDto.selectAdn}
and player_id = #{searchDto.search}
and wx_nick_name like concat(#{searchDto.search},'%')
and request_id = #{searchDto.search}
and errinfo = #{searchDto.search}
and clicks > 1
and clicks = 1
and clicks = 0
and clicks > 0
and request_time between #{searchDto.startTime} and #{searchDto.endTime}
order by ad_stream_id desc
