mapper.update:
UPDATE day_behave_recordlist
SET today_encourageAdv_count = today_encourageAdv_count + #{encourageCount}
WHERE day_id = #{dayid}

UPDATE day_behave_recordlist
SET service_CallBack_AdvCount = service_CallBack_AdvCount + #{callbackCount}
WHERE day_id = #{dayid}

UPDATE day_behave_recordlist
SET service_CallBack_AdvCount = service_CallBack_AdvCount + #{rewardCount},
    totalRed                  = totalRed + #{totalRed},
    todayRed                  = todayRed + #{todayRed}
WHERE day_id = #{dayid}

UPDATE day_behave_recordlist
SET day_cash = day_cash + #{dayCash}
WHERE day_id = #{dayid}

mapper.select:
SELECT todayRed,today_encourageAdv_count,retain_time
FROM day_behave_recordlist
WHERE player_id = #{playerId}

    and date(retain_time) = #{recordTime}

group by date(retain_time)
order by day_id desc

SELECT day_behave_recordlist.player_id ,wx_nickname,retain_time,today_encourageAdv_count
FROM day_behave_recordlist left join player p on day_behave_recordlist.player_id = p.player_id
WHERE day_id=day_id and app_id = #{active.appid}




            and day_behave_recordlist.player_id = #{active.search}





            and p.wx_nickname = #{active.search}






    and retain_time between #{active.startTime} and #{active.endTime}

group by p.player_id, date(retain_time)
order by day_id desc
